using Dapper;\nusing System.Data;\nusing System.Data.SqlClient;\n\npublic IEnumerable<MyEntity> GetEntities(string baseTable, Dictionary<string, object> parameters)\n{\n    using (IDbConnection db = new SqlConnection("YourConnectionString"))\n    {\n        var sql = new StringBuilder($"SELECT * FROM {baseTable} WHERE 1=1");\n\n        DynamicParameters dynamicParameters = new DynamicParameters();\n        foreach (var param in parameters)\n        {\n            sql.Append($" AND {param.Key} = @{param.Key}");\n            dynamicParameters.Add($"@{param.Key}", param.Value);\n        }\n\n        return db.Query<MyEntity>(sql.ToString(), dynamicParameters);\n    }\n}