using MimeDetective;\nusing System.IO;\n\npublic class FileTypeChecker\n{\n    public static bool IsValidFileType(string filePath, string expectedExtension)\n    {\n        var inspector = new ContentInspectorBuilder()\n                            .Definitions(Default.FileTypes.Documents.All())\n                            .Build();\n\n        using (var stream = File.OpenRead(filePath))\n        {\n            var result = inspector.Inspect(stream);\n            return result.ByFileExtension().Equals(expectedExtension, StringComparison.OrdinalIgnoreCase);\n        }\n    }\n}