public class Example\n{\n    public void MethodWithNotNullParameter(string notNullParam)\n    {\n        if (notNullParam == null)\n        {\n            throw new ArgumentNullException(nameof(notNullParam));\n        }\n        \n        Console.WriteLine(notNullParam);\n    }\n\n    public void MethodWithCanBeNullParameter(string? canBeNullParam)\n    {\n        if (canBeNullParam == null)\n        {\n            Console.WriteLine("Parameter is null");\n        }\n        else\n        {\n            Console.WriteLine(canBeNullParam);\n        }\n    }\n}