// Setting a cookie\nHttpCookie cookie = new HttpCookie("ChannelId", channelId);\ncookie.HttpOnly = true;\ncookie.Secure = true; // Ensure this is set only if your site is HTTPS\nHttpContext.Current.Response.Cookies.Add(cookie);\n\n// Retrieving a cookie\nHttpCookie cookie = HttpContext.Current.Request.Cookies["ChannelId"];\nif (cookie != null)\n{\n    var channelId = cookie.Value;\n}