using System;\nusing System.Linq;\nusing System.Xml.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        string htmlTable = @"\n            <table border='0' width='100%'>\n                <tr class='headerbg'>\n                    <th width='5%'>No</th>\n                    <th width='30%'>Name</th>\n                    <th width='20%'>Department or Division</th>\n                    <th width='25%'>Email</th>\n                    <th width='20%'>Staff/Student</th>\n                </tr>\n                <tr class='bg2'>\n                    <td>1</td>\n                    <td><strong><a class='searchLink2' href='tel_search.php?fore=Dave&amp;sur=Rumber'>Dave Rumber</a></strong></td>\n                    <td>Medical School</td>\n                    <td><a class='searchLink2' href='mailto:Dave.Rumber@Home.com'>Dave.Rumber@Home.com</a></td>\n                    <td>Student&nbsp;</td>\n                </tr>\n                <!-- Add more rows as needed -->\n            </table>";\n\n        // Clean up the HTML to make it XML-friendly\n        htmlTable = htmlTable.Replace("&nbsp;", "");\n        htmlTable = htmlTable.Replace("&", "&amp;");\n\n        XElement tableElement = XElement.Parse(htmlTable);\n\n        var rows = tableElement.Elements("tr").Skip(1); // Skip the header row\n\n        foreach (var row in rows)\n        {\n            var columns = row.Elements("td").ToArray();\n            string name = columns[1].Value.Trim();\n            string email = columns[3].Value.Trim();\n\n            Console.WriteLine($"Name: {name}, Email: {email}");\n            // Process further as needed, e.g., add to DataGrid, insert into database, etc.\n        }\n    }\n}