using System;\nusing System.Threading;\n\npublic class Configuration\n{\n    private static readonly object syncLock = new object();\n    private static YourConfigType configData;\n\n    public static YourConfigType ConfigData\n    {\n        get\n        {\n            YourConfigType data;\n            Monitor.Enter(syncLock);\n            try\n            {\n                data = configData;\n            }\n            finally\n            {\n                Monitor.Exit(syncLock);\n            }\n            return data;\n        }\n        set\n        {\n            Monitor.Enter(syncLock);\n            try\n            {\n                configData = value;\n            }\n            finally\n            {\n                Monitor.Exit(syncLock);\n            }\n        }\n    }\n}