using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing Telerik.Web.UI;\n\npublic partial class DynamicForm : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            GenerateDynamicForm();\n        }\n    }\n\n    private void GenerateDynamicForm()\n    {\n        string connectionString = "your_connection_string_here";\n        using (SqlConnection conn = new SqlConnection(connectionString))\n        {\n            conn.Open();\n            SqlCommand cmd = new SqlCommand("SELECT * FROM Panels", conn);\n            SqlDataAdapter da = new SqlDataAdapter(cmd);\n            DataTable dtPanels = new DataTable();\n            da.Fill(dtPanels);\n\n            foreach (DataRow panelRow in dtPanels.Rows)\n            {\n                RadPanelBar panelBar = new RadPanelBar();\n                panelBar.ID = "panelBar" + panelRow["PanelID"];\n                RadPanelItem panelItem = new RadPanelItem(panelRow["PanelName"].ToString());\n\n                SqlCommand fieldCmd = new SqlCommand("SELECT * FROM Fields WHERE PanelID = @PanelID", conn);\n                fieldCmd.Parameters.AddWithValue("@PanelID", panelRow["PanelID"]);\n                SqlDataAdapter fieldDa = new SqlDataAdapter(fieldCmd);\n                DataTable dtFields = new DataTable();\n                fieldDa.Fill(dtFields);\n\n                foreach (DataRow fieldRow in dtFields.Rows)\n                {\n                    string fieldType = fieldRow["FieldType"].ToString();\n                    Control fieldControl;\n                    switch (fieldType)\n                    {\n                        case "TextBox":\n                            fieldControl = new RadTextBox { ID = "txt" + fieldRow["FieldID"], Width = Unit.Percentage(100) };\n                            break;\n                        case "ComboBox":\n                            fieldControl = new RadComboBox { ID = "cmb" + fieldRow["FieldID"], Width = Unit.Percentage(100) };\n                            break;\n                        default:\n                            fieldControl = new Literal { Text = "Unsupported field type" };\n                            break;\n                    }\n                    panelItem.Controls.Add(new Literal { Text = fieldRow["FieldName"] + ": " });\n                    panelItem.Controls.Add(fieldControl);\n                    panelItem.Controls.Add(new Literal { Text = "<br />" });\n                }\n\n                panelBar.Items.Add(panelItem);\n                formPlaceholder.Controls.Add(panelBar);\n            }\n        }\n    }\n}