using System;\nusing System.Diagnostics;\nusing System.IO;\n\npublic class PathConverter\n{\n    public static string GetShortPathName(string longPath)\n    {\n        var processInfo = new ProcessStartInfo("cmd.exe", $"/c for %I in (\"{longPath}\") do @echo %~sI")\n        {\n            RedirectStandardOutput = true,\n            UseShellExecute = false,\n            CreateNoWindow = true\n        };\n\n        using (var process = new Process { StartInfo = processInfo })\n        {\n            process.Start();\n            using (var reader = process.StandardOutput)\n            {\n                string result = reader.ReadToEnd().Trim();\n                process.WaitForExit();\n                return result;\n            }\n        }\n    }\n\n    public static void Main()\n    {\n        string longPath = @"C:\Program Files\Some Long Path\Example.txt";\n        string shortPath = GetShortPathName(longPath);\n        Console.WriteLine(shortPath);\n    }\n}