using System;\nusing System.Collections.Generic;\nusing System.Linq;\nusing System.Windows.Forms;\n\npublic class CustomControl : Control\n{\n    public DateTime Date { get; set; }\n}\n\npublic class MyForm : Form\n{\n    private FlowLayoutPanel flowLayoutPanel;\n\n    public MyForm()\n    {\n        flowLayoutPanel = new FlowLayoutPanel();\n        flowLayoutPanel.ControlAdded += FlowLayoutPanel_ControlAdded;\n        this.Controls.Add(flowLayoutPanel);\n    }\n\n    private void FlowLayoutPanel_ControlAdded(object sender, ControlEventArgs e)\n    {\n        SortControlsByDate();\n    }\n\n    private void SortControlsByDate()\n    {\n        List<Control> controls = flowLayoutPanel.Controls.Cast<Control>().ToList();\n        controls.Sort((x, y) => ((CustomControl)x).Date.CompareTo(((CustomControl)y).Date));\n\n        flowLayoutPanel.SuspendLayout();\n        flowLayoutPanel.Controls.Clear();\n        flowLayoutPanel.Controls.AddRange(controls.ToArray());\n        flowLayoutPanel.ResumeLayout();\n    }\n}