using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        string url = "http://www.example.com";\n        \n        try\n        {\n            Process.Start(new ProcessStartInfo\n            {\n                FileName = url,\n                UseShellExecute = true\n            });\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n    }\n}