using System;\nusing System.Diagnostics;\nusing System.Threading;\n\nclass Program\n{\n    static void Main()\n    {\n        PerformanceCounter cpuCounter = new PerformanceCounter("Process", "% Processor Time", Process.GetCurrentProcess().ProcessName);\n        cpuCounter.NextValue(); // Initial call to set up the counter\n\n        Timer timer = new Timer(LogCpuUsage, cpuCounter, 0, 1000); // Log every second\n        Console.WriteLine("Press any key to exit...");\n        Console.ReadKey();\n    }\n\n    private static void LogCpuUsage(object state)\n    {\n        PerformanceCounter cpuCounter = (PerformanceCounter)state;\n        float cpuUsage = cpuCounter.NextValue() / Environment.ProcessorCount; // Divide by the number of processors\n        Console.WriteLine($"CPU Usage: {cpuUsage}%");\n\n        // Here you can log the CPU usage to SmartInspect or any other logging mechanism\n    }\n}