using System;\nusing System.Diagnostics;\nusing System.IO;\nusing PdfSharp.Pdf.Printing;\n\nnamespace PrintPdfFile\n{\n    class Program\n    {\n        [STAThread]\n        static void Main(string[] args)\n        {\n            // Set Acrobat Reader EXE, e.g.:\n            PdfFilePrinter.AdobeReaderPath = @"C:\\Program Files\\Adobe\\Acrobat Reader DC\\Reader\\AcroRd32.exe";\n\n            // Set the file to print and the Windows name of the printer.\n            // Make sure the paths and printer name are correct.\n            string pdfFilePath = @"C:\\Documents and Settings\\mike.smith\\Desktop\\Stuff\\ReleaseNotesAndFolderList.pdf";\n            string printerName = @"\\ny-dc-03\IT-01";\n\n            if (!File.Exists(pdfFilePath))\n            {\n                Console.WriteLine("Error: PDF file not found at " + pdfFilePath);\n                return;\n            }\n\n            if (!File.Exists(PdfFilePrinter.AdobeReaderPath))\n            {\n                Console.WriteLine("Error: Adobe Reader executable not found at " + PdfFilePrinter.AdobeReaderPath);\n                return;\n            }\n\n            PdfFilePrinter printer = new PdfFilePrinter(pdfFilePath, printerName);\n\n            try\n            {\n                printer.Print();\n                Console.WriteLine("Print job sent successfully.");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("Error: " + ex.Message);\n            }\n        }\n    }\n}