using System;\nusing System.Data;\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Assuming 'dataTable' is your DataTable object\n        DataTable dataTable = GetDataTable(); // Replace with your method to get the DataTable\n\n        // Check if there are any rows in the DataTable\n        if (dataTable.Rows.Count > 0)\n        {\n            // Access the first row\n            DataRow firstRow = dataTable.Rows[0];\n\n            // Retrieve the value from the desired column, e.g., "ColumnName"\n            // Replace "ColumnName" with the actual name of the column you are interested in\n            object value = firstRow["ColumnName"];\n\n            // Optionally, convert the value to the expected type, e.g., string\n            string stringValue = value.ToString();\n\n            Console.WriteLine("The value is: " + stringValue);\n        }\n        else\n        {\n            Console.WriteLine("The DataTable is empty.");\n        }\n    }\n\n    // Dummy method to represent retrieving a DataTable\n    private static DataTable GetDataTable()\n    {\n        DataTable table = new DataTable();\n        table.Columns.Add("ColumnName", typeof(string));\n        table.Rows.Add("SampleValue");\n        return table;\n    }\n}