using System;\nusing System.IO;\nusing System.Windows.Forms;\n\npublic class FileListerForm : Form\n{\n    private CheckedListBox checkedListBox;\n    private Button processButton;\n\n    public FileListerForm()\n    {\n        checkedListBox = new CheckedListBox();\n        checkedListBox.Dock = DockStyle.Top;\n        this.Controls.Add(checkedListBox);\n\n        processButton = new Button();\n        processButton.Text = "Process Selected Files";\n        processButton.Dock = DockStyle.Bottom;\n        processButton.Click += ProcessButton_Click;\n        this.Controls.Add(processButton);\n\n        LoadFiles(@"C:\YourDirectoryPath"); // Change to your directory path\n    }\n\n    private void LoadFiles(string directoryPath)\n    {\n        if (Directory.Exists(directoryPath))\n        {\n            string[] files = Directory.GetFiles(directoryPath);\n            checkedListBox.Items.AddRange(files);\n        }\n        else\n        {\n            MessageBox.Show("Directory does not exist.");\n        }\n    }\n\n    private void ProcessButton_Click(object sender, EventArgs e)\n    {\n        foreach (string file in checkedListBox.CheckedItems)\n        {\n            // Perform your operations on each selected file\n            MessageBox.Show($"Processing file: {file}");\n        }\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new FileListerForm());\n    }\n}