using System;\nusing Microsoft.Win32;\n\nclass Program\n{\n    static void Main()\n    {\n        string iisVersion = GetIISVersion();\n        if (!string.IsNullOrEmpty(iisVersion))\n        {\n            Console.WriteLine("IIS Version: " + iisVersion);\n        }\n        else\n        {\n            Console.WriteLine("IIS is not installed.");\n        }\n    }\n\n    static string GetIISVersion()\n    {\n        string registryPath = @"SOFTWARE\Microsoft\InetStp";\n        using (RegistryKey key = Registry.LocalMachine.OpenSubKey(registryPath))\n        {\n            if (key != null)\n            {\n                object versionObj = key.GetValue("VersionString");\n                if (versionObj != null)\n                {\n                    return versionObj.ToString();\n                }\n            }\n        }\n        return null;\n    }\n}