using System;\nusing System.Globalization;\n\npublic class DateRangeValidator\n{\n    public static bool ValidateDateRange(string dateRange)\n    {\n        if (string.IsNullOrEmpty(dateRange))\n        {\n            return false;\n        }\n\n        string[] dates = dateRange.Split(new string[] { " - " }, StringSplitOptions.None);\n\n        if (dates.Length != 2)\n        {\n            return false;\n        }\n\n        DateTime startDate;\n        DateTime endDate;\n        bool isStartDateValid = DateTime.TryParseExact(dates[0], "MM/dd/yyyy", CultureInfo.InvariantCulture, DateTimeStyles.None, out startDate);\n        bool isEndDateValid = DateTime.TryParseExact(dates[1], "MM/dd/yyyy", CultureInfo.InvariantCulture, DateTimeStyles.None, out endDate);\n\n        if (!isStartDateValid || !isEndDateValid)\n        {\n            return false;\n        }\n\n        if (startDate > endDate)\n        {\n            return false;\n        }\n\n        return true;\n    }\n}