using System;\nusing System.IO;\n\npublic class Program\n{\n    public static bool IsJpeg(string filePath)\n    {\n        if (!File.Exists(filePath))\n        {\n            return false;\n        }\n\n        byte[] buffer = new byte[4];\n        using (FileStream fs = new FileStream(filePath, FileMode.Open, FileAccess.Read))\n        {\n            if (fs.Length < 4)\n            {\n                return false;\n            }\n\n            fs.Read(buffer, 0, 2);\n            if (buffer[0] == 0xFF && buffer[1] == 0xD8)\n            {\n                fs.Seek(-2, SeekOrigin.End);\n                fs.Read(buffer, 0, 2);\n                if (buffer[0] == 0xFF && buffer[1] == 0xD9)\n                {\n                    return true;\n                }\n            }\n        }\n\n        return false;\n    }\n\n    public static void Main()\n    {\n        string filePath = "path_to_your_file.jpg";\n        bool isJpeg = IsJpeg(filePath);\n        Console.WriteLine($"Is the file a JPEG? {isJpeg}");\n    }\n}