protected void ShowVideos(object sender, EventArgs e)\n{\n    // Load and display the Videos control\n    LoadControl("~/Controls/Videos.ascx");\n}\n\nprotected void ShowArtists(object sender, EventArgs e)\n{\n    // Load and display the Artists control\n    LoadControl("~/Controls/Artists.ascx");\n}\n\nprotected void ShowGenres(object sender, EventArgs e)\n{\n    // Load and display the Genres control\n    LoadControl("~/Controls/Genres.ascx");\n}\n\nprivate void LoadControl(string controlPath)\n{\n    Control control = Page.LoadControl(controlPath);\n    Placeholder.Controls.Clear();\n    Placeholder.Controls.Add(control);\n}