using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    static void Main()\n    {\n        long width = 19005;\n        long height = 32768;\n        long size = width * height * 4;\n\n        // Allocate unmanaged memory\n        IntPtr bufferPtr = Marshal.AllocHGlobal(new IntPtr(size));\n\n        try\n        {\n            // Use the allocated memory (example: set the first byte to 255)\n            Marshal.WriteByte(bufferPtr, 0, 255);\n\n            // You can also copy managed arrays to this unmanaged memory if needed\n            byte[] managedBuffer = new byte[size];\n            Marshal.Copy(managedBuffer, 0, bufferPtr, managedBuffer.Length);\n\n            // Perform operations on the buffer...\n\n        }\n        finally\n        {\n            // Free the unmanaged memory\n            Marshal.FreeHGlobal(bufferPtr);\n        }\n    }\n}