using System.IO;\nusing System.Web;\nusing System.Web.UI;\n\npublic string RenderUserControl(string controlPath)\n{\n    // Create a dummy Page instance\n    Page page = new Page();\n    \n    // Create a dummy form and add it to the page\n    HtmlForm form = new HtmlForm();\n    page.Controls.Add(form);\n    \n    // Load the UserControl\n    UserControl myControl = (UserControl)page.LoadControl(controlPath);\n    \n    // Add the UserControl to the form\n    form.Controls.Add(myControl);\n    \n    // Render the page, including the UserControl, to a string\n    using (StringWriter stringWriter = new StringWriter())\n    {\n        using (HtmlTextWriter htmlWriter = new HtmlTextWriter(stringWriter))\n        {\n            page.RenderControl(htmlWriter);\n        }\n        \n        return stringWriter.ToString();\n    }\n}