using System;\nusing AzRoles;\n\nclass Program\n{\n    static void Main()\n    {\n        string storeName = "msxml://C:\\AzManStore.xml"; // Update with your AzMan store path\n        string appName = "YourAppName"; // Update with your application name\n        string userName = "DOMAIN\\UserName"; // Update with your user name\n\n        IAzAuthorizationStore3 azStore = new AzAuthorizationStoreClass();\n        azStore.Initialize(0, storeName, null);\n\n        IAzApplication app = azStore.OpenApplication(appName, null);\n        IAzClientContext3 clientContext = (IAzClientContext3)app.InitializeClientContextFromName(userName, null);\n\n        foreach (IAzOperation operation in app.Operations)\n        {\n            if (clientContext.AccessCheck2("YourScopeName", null, operation.Name, null, null, null, null, null, null) == 0)\n            {\n                Console.WriteLine("User has access to operation: " + operation.Name);\n            }\n        }\n    }\n}