using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing IBM.Data.DB2;\n\nclass Program\n{\n    static void Main()\n    {\n        // Connection strings\n        string db2ConnectionString = "Your DB2 Connection String";\n        string sqlServerConnectionString = "Your SQL Server Connection String";\n\n        // Query to fetch data from DB2\n        string db2Query = "SELECT tabschema, tabname, colname, typename, length FROM syscat.columns WHERE ...restrict to needed table...";\n\n        // Fetch data from DB2\n        DataTable dataTable = new DataTable();\n        using (DB2Connection db2Connection = new DB2Connection(db2ConnectionString))\n        {\n            DB2Command command = new DB2Command(db2Query, db2Connection);\n            DB2DataAdapter adapter = new DB2DataAdapter(command);\n            adapter.Fill(dataTable);\n        }\n\n        // Transform data if necessary to match your destination table structure\n        DataTable transformedTable = TransformDataTable(dataTable);\n\n        // Bulk insert into SQL Server\n        using (SqlConnection sqlConnection = new SqlConnection(sqlServerConnectionString))\n        {\n            sqlConnection.Open();\n            using (SqlBulkCopy bulkCopy = new SqlBulkCopy(sqlConnection))\n            {\n                bulkCopy.DestinationTableName = "all_table_information";\n                bulkCopy.ColumnMappings.Add("tabschema", "table_id");\n                bulkCopy.ColumnMappings.Add("tabname", "column_name");\n                bulkCopy.ColumnMappings.Add("typename", "data_type");\n                bulkCopy.ColumnMappings.Add("length", "max_char_length");\n\n                bulkCopy.WriteToServer(transformedTable);\n            }\n        }\n    }\n\n    private static DataTable TransformDataTable(DataTable dataTable)\n    {\n        // Implement any necessary transformation logic here\n        // For example, renaming columns or changing data types\n        \n        // This example assumes no transformation is needed\n        return dataTable;\n    }\n}