using System;\nusing System.Data;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        // Sample XML\n        string xml = @"\n        <item attr='some attribute'>\n            <description>anything</description>\n        </item>";\n\n        // Load XML into DataSet\n        DataSet ds = new DataSet();\n        using (StringReader sr = new StringReader(xml))\n        {\n            ds.ReadXml(sr);\n        }\n\n        // Access the DataTable for 'item' element\n        DataTable itemTable = ds.Tables["item"];\n\n        // Access the first DataRow\n        DataRow row = itemTable.Rows[0];\n\n        // Retrieve the 'attr' value\n        string attributeValue = row["attr"].ToString();\n\n        // Output the attribute value\n        Console.WriteLine("Attribute value: " + attributeValue);\n    }\n}