private string _data;\n\npublic string Data\n{\n    get\n    {\n        // Unacceptable: This could be a long-running operation\n        using (var connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n            using (var command = new SqlCommand("SELECT Data FROM Table WHERE Id = 1", connection))\n            {\n                _data = (string)command.ExecuteScalar();\n            }\n        }\n        return _data;\n    }\n}