using System;\nusing System.Net.Http;\nusing System.Net.Http.Headers;\nusing System.Threading.Tasks;\n\npublic class FileUploadHelper\n{\n    public async Task PostFileAsync(string url, byte[] fileBytes, string fileName, string apiKey, string saveUrl, string id, string format)\n    {\n        using (var client = new HttpClient())\n        {\n            using (var content = new MultipartFormDataContent())\n            {\n                // Add the file content\n                var fileContent = new ByteArrayContent(fileBytes);\n                fileContent.Headers.ContentDisposition = new ContentDispositionHeaderValue("form-data")\n                {\n                    Name = "\"content\"",\n                    FileName = "\"" + fileName + "\""\n                };\n                fileContent.Headers.ContentType = new MediaTypeHeaderValue("application/octet-stream");\n                content.Add(fileContent);\n\n                // Add other form data\n                content.Add(new StringContent(fileName), "\"filename\"");\n                content.Add(new StringContent(saveUrl), "\"saveurl\"");\n                content.Add(new StringContent(id), "\"id\"");\n                content.Add(new StringContent(format), "\"format\"");\n\n                // Send the POST request\n                var response = await client.PostAsync(url, content);\n                response.EnsureSuccessStatusCode();\n\n                // Optionally read the response\n                var responseString = await response.Content.ReadAsStringAsync();\n                Console.WriteLine(responseString);\n            }\n        }\n    }\n}