using System;\nusing System.Drawing;\n\nclass Program\n{\n    static void Main()\n    {\n        // Load background and text images\n        Bitmap background = new Bitmap("background.jpg");\n        Bitmap textImage = new Bitmap("textImage.jpg");\n\n        // Create a new bitmap to hold the final image\n        Bitmap finalImage = new Bitmap(background.Width, background.Height);\n\n        // Create graphics object from the final image\n        using (Graphics g = Graphics.FromImage(finalImage))\n        {\n            // Draw the background image\n            g.DrawImage(background, 0, 0);\n\n            // Set the white color in the text image to be transparent\n            textImage.MakeTransparent(Color.White);\n\n            // Draw the text image over the background\n            g.DrawImage(textImage, 0, 0);\n        }\n\n        // Save the final image to a file\n        finalImage.Save("mergedImage.jpg");\n    }\n}