using System;\nusing System.Net.Mail;\nusing System.Text.RegularExpressions;\n\npublic void SendSecureEmail(string userEmail, string userName, string userMessage)\n{\n    // Regular expression to validate email\n    string emailPattern = @"^[^@\s]+@[^@\s]+\.[^@\s]+$";\n    if (!Regex.IsMatch(userEmail, emailPattern))\n    {\n        throw new ArgumentException("Invalid email address");\n    }\n    \n    // Validate other input fields to prevent injection\n    if (string.IsNullOrEmpty(userName) || string.IsNullOrEmpty(userMessage))\n    {\n        throw new ArgumentException("Invalid input");\n    }\n    \n    // Create the mail message\n    MailMessage mailMessage = new MailMessage();\n    mailMessage.From = new MailAddress("your-email@example.com");\n    mailMessage.To.Add(userEmail);\n    mailMessage.Subject = "Subject Here";\n    mailMessage.Body = $"Hello {userName},\n\n{userMessage}";\n    \n    // Configure the SMTP client\n    SmtpClient smtpClient = new SmtpClient("your-smtp-server");\n    smtpClient.Port = 587; // Adjust the port as needed\n    smtpClient.Credentials = new System.Net.NetworkCredential("your-smtp-username", "your-smtp-password");\n    smtpClient.EnableSsl = true; // Enable SSL if required\n    \n    // Send the email\n    try\n    {\n        smtpClient.Send(mailMessage);\n    }\n    catch (SmtpException ex)\n    {\n        // Handle any exceptions that occur during the sending process\n        Console.WriteLine($"Error sending email: {ex.Message}");\n    }\n}