using System;\nusing System.Diagnostics;\nusing System.Linq;\nusing System.Windows.Forms;\n\npublic partial class MainForm : Form\n{\n    private Timer statusTimer;\n    private string vpnProcessName = "yourVpnProcessName"; // Replace with your VPN process name\n\n    public MainForm()\n    {\n        InitializeComponent();\n\n        // Initialize Timer\n        statusTimer = new Timer();\n        statusTimer.Interval = 5000; // Check every 5 seconds\n        statusTimer.Tick += new EventHandler(CheckVpnStatus);\n        statusTimer.Start();\n    }\n\n    private void CheckVpnStatus(object sender, EventArgs e)\n    {\n        bool isVpnRunning = Process.GetProcessesByName(vpnProcessName).Any();\n        UpdateStatusControl(isVpnRunning);\n    }\n\n    private void UpdateStatusControl(bool isVpnRunning)\n    {\n        if (isVpnRunning)\n        {\n            statusLabel.Text = "VPN Connected";\n            statusLabel.ForeColor = Color.Green;\n        }\n        else\n        {\n            statusLabel.Text = "VPN Disconnected";\n            statusLabel.ForeColor = Color.Red;\n        }\n    }\n}