using Microsoft.Win32;\n\npublic void CopySettingsToCurrentUser()\n{\n    string subkey = @"SOFTWARE\YourCompany\YourApp";\n    using (RegistryKey localMachineKey = Registry.LocalMachine.OpenSubKey(subkey))\n    {\n        if (localMachineKey != null)\n        {\n            using (RegistryKey currentUserKey = Registry.CurrentUser.CreateSubKey(subkey))\n            {\n                if (currentUserKey != null)\n                {\n                    foreach (string valueName in localMachineKey.GetValueNames())\n                    {\n                        object value = localMachineKey.GetValue(valueName);\n                        RegistryValueKind valueKind = localMachineKey.GetValueKind(valueName);\n                        currentUserKey.SetValue(valueName, value, valueKind);\n                    }\n                }\n            }\n        }\n    }\n}