using System;\nusing System.Text.RegularExpressions;\n\npublic class HyperlinkParser\n{\n    public static void Main()\n    {\n        string pattern = @"<a\s+(?:[^>]*?\s+)?href\s*=\s*([""'])\s*(?<url>.*?)\s*\1[^>]*>(?<text>.*?)<\/a>";\n        string input = @"<a href='http://example.com'>Example</a>\n                         <a href='http://example.com'><b>Example</b></a>\n                         <a href='http://example.com' >Example with spaces</a>";\n\n        RegexOptions options = RegexOptions.IgnoreCase | RegexOptions.Singleline;\n\n        foreach (Match m in Regex.Matches(input, pattern, options))\n        {\n            Console.WriteLine("URL: " + m.Groups["url"].Value);\n            Console.WriteLine("Description: " + m.Groups["text"].Value);\n        }\n    }\n}