public interface IVehicle\n{\n    void Drive();\n    void Stop();\n    // Other methods...\n}\n\npublic class Car : IVehicle\n{\n    public void Drive()\n    {\n        // Implementation of Drive method\n    }\n\n    public void Stop()\n    {\n        // Implementation of Stop method\n    }\n    \n    // Other methods...\n}\n\npublic class VehicleDecorator : IVehicle\n{\n    private readonly IVehicle _vehicle;\n    private int _callCount;\n\n    public VehicleDecorator(IVehicle vehicle)\n    {\n        _vehicle = vehicle;\n        _callCount = 0;\n    }\n\n    public void Drive()\n    {\n        _callCount++;\n        _vehicle.Drive();\n    }\n\n    public void Stop()\n    {\n        _callCount++;\n        _vehicle.Stop();\n    }\n    \n    // Other methods...\n\n    public int GetCallCount()\n    {\n        return _callCount;\n    }\n}