// Global.asax.cs\n\nvoid Session_Start(object sender, EventArgs e)\n{\n    // Store the session start time\n    Session["SessionStartTime"] = DateTime.Now;\n}\n\npublic TimeSpan GetSessionAge()\n{\n    if (Session["SessionStartTime"] != null)\n    {\n        DateTime sessionStartTime = (DateTime)Session["SessionStartTime"];\n        return DateTime.Now - sessionStartTime;\n    }\n    else\n    {\n        throw new InvalidOperationException("Session start time not available.");\n    }\n}