using System;\nusing System.Collections.Generic;\n\npublic interface I\n{\n    // Interface members\n}\n\npublic class T : I\n{\n    // Class members\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        List<T[]> arrays = new List<T[]>\n        {\n            new T[] { new T(), new T() },\n            new T[] { new T() }\n        };\n\n        List<I> list = new List<I>();\n        foreach (T[] arrayOfA in arrays)\n        {\n            list.AddRange(arrayOfA); // No need to convert because T implements I\n        }\n\n        I[] result = list.ToArray();\n        \n        // Output the result length for verification\n        Console.WriteLine(result.Length);\n    }\n}