public class PluginViewEngine : RazorViewEngine\n   {\n       public PluginViewEngine()\n       {\n           // Add plugin view locations\n           var pluginViewLocations = new[]\n           {\n               "~/Plugins/{1}/Views/{0}.cshtml",\n               "~/Plugins/Shared/Views/{0}.cshtml"\n           };\n           ViewLocationFormats = ViewLocationFormats.Union(pluginViewLocations).ToArray();\n           PartialViewLocationFormats = PartialViewLocationFormats.Union(pluginViewLocations).ToArray();\n       }\n   }