using System.Web.Mvc;\nusing System.Linq;\nusing System.Web;\n\npublic class HostHeaderFilter : ActionFilterAttribute\n{\n    private static readonly Dictionary<string, string> HostHeaders = new Dictionary<string, string>\n    {\n        { "hello.mydomain.com", "Hello World" },\n        { "goodbye.mydomain.com", "Goodbye World" }\n    };\n\n    public override void OnActionExecuting(ActionExecutingContext filterContext)\n    {\n        var host = HttpContext.Current.Request.Url.Host.ToLower();\n\n        if (HostHeaders.TryGetValue(host, out string pageTitle))\n        {\n            filterContext.Controller.ViewBag.PageTitle = pageTitle;\n        }\n        else\n        {\n            filterContext.Result = new RedirectResult("~/Error/NoHostHeader");\n        }\n    }\n}