using Ganss.XSS;\n\npublic ActionResult DisplayHtmlContent()\n{\n    // Assume you have retrieved the raw HTML content from the database.\n    string rawHtmlContent = "<pre>This is some <script>alert('XSS');</script>sample text</pre>";\n\n    // Sanitize the HTML content to prevent XSS attacks.\n    var sanitizer = new HtmlSanitizer();\n    string sanitizedHtmlContent = sanitizer.Sanitize(rawHtmlContent);\n\n    // Pass the sanitized HTML content to the view.\n    ViewData["HtmlContent"] = sanitizedHtmlContent;\n\n    return View();\n}