using System;\nusing System.Runtime.InteropServices;\nusing System.Text;\n\npublic class cnOCRsdk\n{\n    [StructLayout(LayoutKind.Sequential, CharSet = CharSet.Unicode)]\n    public struct RECO_DATA\n    {\n        [MarshalAs(UnmanagedType.ByValTStr, SizeConst = 200)]\n        public string Surname;\n        [MarshalAs(UnmanagedType.ByValTStr, SizeConst = 200)]\n        public string FirstName;\n    }\n\n    [DllImport("cnOCRsdk.dll", EntryPoint = "recoCHN_P_Name", CharSet = CharSet.Ansi, CallingConvention = CallingConvention.Cdecl)]\n    public static extern bool recoCHN_P_Name(string imgPath, ref RECO_DATA o_data);\n}\n\nclass Program\n{\n    static void Main()\n    {\n        cnOCRsdk.RECO_DATA recoData = new cnOCRsdk.RECO_DATA();\n        string path = @"C:\WINDOWS\twain_32\twainrgb.bmp";\n        bool res = cnOCRsdk.recoCHN_P_Name(path, ref recoData);\n\n        if (res)\n        {\n            Console.WriteLine("First Name: " + recoData.FirstName);\n            Console.WriteLine("Surname: " + recoData.Surname);\n        }\n        else\n        {\n            Console.WriteLine("Recognition failed.");\n        }\n    }\n}