using System;\n   using System.Data.SqlClient;\n   using System.Web.Caching;\n   using SubSonic;\n\n   public class YourRepository\n   {\n       public YourModel GetYourData()\n       {\n           string cacheKey = "YourDataCacheKey";\n           YourModel data = HttpContext.Current.Cache[cacheKey] as YourModel;\n\n           if (data == null)\n           {\n               // Fetch data using SubSonic\n               data = new Select().From<YourModel>().ExecuteSingle<YourModel>();\n\n               // Set up SQL cache dependency\n               string connectionString = ConfigurationManager.ConnectionStrings["YourConnectionString"].ConnectionString;\n               SqlCacheDependency sqlDependency = new SqlCacheDependency("YourDatabaseName", "YourTableName");\n\n               // Insert data into cache with dependency\n               HttpContext.Current.Cache.Insert(\n                   cacheKey,\n                   data,\n                   sqlDependency,\n                   Cache.NoAbsoluteExpiration,\n                   TimeSpan.FromMinutes(10)\n               );\n           }\n\n           return data;\n       }\n   }