[ApiController]\n[Route("api/[controller]")]\npublic class TransactionsController : ControllerBase\n{\n    [HttpGet("{id}")]\n    public IActionResult GetTransactionStatus(int id)\n    {\n        // Logic to get transaction status\n        var transactionStatus = new { Id = id, Status = "Completed" };\n        return Ok(transactionStatus);\n    }\n\n    [HttpPost]\n    public IActionResult CreateTransaction([FromBody] TransactionRequest request)\n    {\n        // Logic to create a new transaction\n        var newTransaction = new { Id = 123, Status = "Pending" };\n        return CreatedAtAction(nameof(GetTransactionStatus), new { id = newTransaction.Id }, newTransaction);\n    }\n}\n\npublic class TransactionRequest\n{\n    public string Username { get; set; }\n    public string Password { get; set; }\n    public string Data { get; set; }\n}