using System;\nusing System.ComponentModel;\nusing System.Configuration.Install;\nusing System.ServiceProcess;\n\n[RunInstaller(true)]\npublic class MyServiceInstaller : Installer\n{\n    public MyServiceInstaller()\n    {\n        ServiceProcessInstaller processInstaller = new ServiceProcessInstaller();\n        ServiceInstaller serviceInstaller = new ServiceInstaller();\n\n        // Set the account type for the service\n        processInstaller.Account = ServiceAccount.User;\n\n        // Set the username and password for the service\n        processInstaller.Username = @"DOMAIN\Username";\n        processInstaller.Password = "Password";\n\n        // Set the service properties\n        serviceInstaller.StartType = ServiceStartMode.Manual;\n        serviceInstaller.ServiceName = "MyServiceName";\n        serviceInstaller.DisplayName = "My Service Display Name";\n        serviceInstaller.Description = "Description of My Service";\n\n        // Add the installers to the Installer collection\n        Installers.Add(processInstaller);\n        Installers.Add(serviceInstaller);\n    }\n}