public class FormService\n   {\n       private readonly IEmailService _emailService;\n       private readonly IFormRepository _formRepository;\n       \n       public FormService(IEmailService emailService, IFormRepository formRepository)\n       {\n           _emailService = emailService;\n           _formRepository = formRepository;\n       }\n\n       public async Task SubmitFormAsync(FormModel form)\n       {\n           // Save form to DB\n           await _formRepository.SaveFormAsync(form);\n\n           // Send email with form details\n           await _emailService.SendFormEmailAsync(form);\n       }\n   }