using System;\nusing System.DirectoryServices.Protocols;\nusing System.Net;\n\nclass Program\n{\n    static void Main()\n    {\n        string ldapServer = "your-ad-server-address";\n        int ldapPort = 389; // default LDAP port\n\n        try\n        {\n            LdapDirectoryIdentifier identifier = new LdapDirectoryIdentifier(ldapServer, ldapPort);\n            using (LdapConnection connection = new LdapConnection(identifier))\n            {\n                connection.AuthType = AuthType.Anonymous; // or use AuthType.Basic for authenticated bind\n                connection.Bind(); // Attempt to bind to the server\n\n                Console.WriteLine("Active Directory server is up and running.");\n            }\n        }\n        catch (LdapException ex)\n        {\n            Console.WriteLine($"Failed to connect to the Active Directory server: {ex.Message}");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n    }\n}