protected void Save(..)\n{   \n    // Do save stuff\n    DisplayAlertAndRedirect("The changes were saved Successfully", "Default.aspx");\n}\n\nprotected virtual void DisplayAlertAndRedirect(string message, string redirectUrl)\n{\n    string script = $@"\n        alert('{message.Replace("'", @"\'").Replace("\n", "\\n").Replace("\r", "\\r")}');\n        window.location='{redirectUrl}';\n    ";\n\n    ClientScript.RegisterStartupScript(\n                    this.GetType(),\n                    Guid.NewGuid().ToString(),\n                    script,\n                    true\n                );\n}