using System;\nusing MySql.Data.MySqlClient;\n\nnamespace ConsoleApp\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            string connectionString = "server=your_server;user=your_username;database=your_database;port=your_port;password=your_password";\n            MySqlConnection connection = new MySqlConnection(connectionString);\n\n            try\n            {\n                connection.Open();\n\n                Console.Write("Enter value to store in the database: ");\n                string userInput = Console.ReadLine();\n\n                string query = "INSERT INTO your_table_name (your_column_name) VALUES (@value)";\n                MySqlCommand cmd = new MySqlCommand(query, connection);\n                cmd.Parameters.AddWithValue("@value", userInput);\n\n                int result = cmd.ExecuteNonQuery();\n\n                if (result > 0)\n                {\n                    Console.WriteLine("Data inserted successfully!");\n                }\n                else\n                {\n                    Console.WriteLine("Failed to insert data.");\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("An error occurred: " + ex.Message);\n            }\n            finally\n            {\n                connection.Close();\n            }\n        }\n    }\n}