using System;\nusing System.IO;\n\npublic class FileUtil\n{\n    public static bool IsFileInUse(string filePath)\n    {\n        try\n        {\n            using (FileStream stream = new FileStream(filePath, FileMode.Open, FileAccess.ReadWrite, FileShare.None))\n            {\n                // If we can open the file with exclusive access, it is not in use\n                return false;\n            }\n        }\n        catch (IOException)\n        {\n            // If an IOException is thrown, the file is in use\n            return true;\n        }\n    }\n}\n\n// Usage example\npublic class Program\n{\n    public static void Main()\n    {\n        string filePath = @"\\network_drive\path\to\your\file.txt";\n        bool isInUse = FileUtil.IsFileInUse(filePath);\n\n        Console.WriteLine(isInUse ? "File is in use." : "File is not in use.");\n    }\n}