using System;\nusing System.Data.SqlClient;\nusing Excel = Microsoft.Office.Interop.Excel;\n\nprivate void ThisAddIn_Startup(object sender, System.EventArgs e)\n{\n    Excel.Worksheet worksheet = (Excel.Worksheet)Application.ActiveSheet;\n    Excel.Range cell = worksheet.Range["A1"];\n    string cellData = cell.Value.ToString();\n    \n    string connectionString = "Server=YourServerName;Database=YourDatabaseName;Trusted_Connection=True;";\n    using (SqlConnection connection = new SqlConnection(connectionString))\n    {\n        connection.Open();\n        string query = "INSERT INTO YourTableName (YourColumnName) VALUES (@cellData)";\n        using (SqlCommand command = new SqlCommand(query, connection))\n        {\n            command.Parameters.AddWithValue("@cellData", cellData);\n            command.ExecuteNonQuery();\n        }\n    }\n\n    System.Windows.Forms.MessageBox.Show("Data exported successfully!");\n}\n\nprivate void ThisAddIn_Shutdown(object sender, System.EventArgs e)\n{\n}\n\nprivate void InternalStartup()\n{\n    this.Startup += new System.EventHandler(ThisAddIn_Startup);\n    this.Shutdown += new System.EventHandler(ThisAddIn_Shutdown);\n}