<asp:Repeater ID="rptLessons" runat="server">\n    <ItemTemplate>\n        <tr>\n            <td><%# Eval("fullname") %></td>\n            <td><%# isCompleted(GetValue(Eval("totallessons")), GetValue(Eval("completedlessons")), GetValue(Eval("totalNumAvail")), GetValue(Eval("totalNumCorrect"))) %></td>\n            <td><%# FormatPercent(GetValue(Eval("totalnumcorrect")) / GetValue(Eval("totalNumAvail"))) %> <%-- (<%# Eval("totalnumcorrect") %> / <%# Eval("totalNumAvail") %>) --%></td>\n            <td><%# FormatPercent(GetValue(Eval("completedlessons")) / GetValue(Eval("totallessons"))) %> <%-- (<%# Eval("completedlessons") %> / <%# Eval("totallessons") %>) --%></td>\n            <td><%# Eval("lastaccessed") %></td>\n        </tr>\n    </ItemTemplate>\n</asp:Repeater>\n\n<script runat="server">\n    protected object GetValue(object value)\n    {\n        return Convert.IsDBNull(value) ? 0 : value;\n    }\n</script>