Chilkat.Crypt2 crypt = new Chilkat.Crypt2();\ncrypt.CryptAlgorithm = "aes";\ncrypt.CipherMode = "cbc";\ncrypt.KeyLength = 256;\ncrypt.PaddingScheme = 0;\ncrypt.EncodingMode = "hex";\n\n// Set the secret key, IV, etc.\ncrypt.SetEncodedKey("your-hex-encoded-key", "hex");\ncrypt.SetEncodedIV("your-hex-encoded-iv", "hex");\n\nstring encryptedText = crypt.EncryptStringENC("Hello, World!");\nstring decryptedText = crypt.DecryptStringENC(encryptedText);