using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing Microsoft.Reporting.WebForms;\n\npublic partial class ReportPage : System.Web.UI.Page\n{\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        if (!IsPostBack)\n        {\n            // Load data into the DataSet\n            DataSet myDataSet = new DataSet();\n            using (SqlConnection conn = new SqlConnection("your_connection_string"))\n            {\n                using (SqlCommand cmd = new SqlCommand("your_stored_procedure", conn))\n                {\n                    cmd.CommandType = CommandType.StoredProcedure;\n                    SqlDataAdapter adapter = new SqlDataAdapter(cmd);\n                    adapter.Fill(myDataSet, "YourDataTableName");\n                }\n            }\n\n            // Set the ReportViewer DataSource\n            ReportViewer1.LocalReport.DataSources.Clear();\n            ReportDataSource rds = new ReportDataSource("YourDataSetName", myDataSet.Tables["YourDataTableName"]);\n            ReportViewer1.LocalReport.DataSources.Add(rds);\n            ReportViewer1.LocalReport.Refresh();\n        }\n    }\n}