<%@ Page Language="C#" %>\n<!DOCTYPE html>\n\n<script runat="server">\n    protected void Page_Load(object sender, EventArgs e)\n    {\n        // Get values from the form\n        string myValue = Request.Form["myvalue"];\n\n        // Perform validation\n        if (string.IsNullOrEmpty(myValue))\n        {\n            // If validation fails, transfer back to the original form\n            Server.Transfer("form.html");\n        }\n        else\n        {\n            // If validation succeeds, redirect to success page\n            Response.Redirect("success.html");\n        }\n    }\n</script>\n<html>\n<head runat="server">\n    <title>Process Form</title>\n</head>\n<body>\n</body>\n</html>